<script type='text/javascript'>jQuery(document).ready(function($) {	/***record impressions***/	var impression_data = {		action: 'lp_record_impression',		current_url: '<?php echo trim(str_replace('//','/',"http://".$_SERVER["HTTP_HOST"].$_SERVER["REQUEST_URI"]."/")); ?>'	};		jQuery.post('<?php echo LANDINGPAGES_URLPATH . 'modules/module.ajax-handler.php'; ?>', impression_data, function(response) {		//alert(response);		if (response!=false)		{			//alert('Got this from the server: ' + response + myajax.ajaxurl);			//form.submit();			return true;		}					});				/***look for form conversion elements and record action***/	var form = jQuery('.lp-form-track');	var lp_stop=0;	var conversion_data = {		action: 'lp_record_conversion',		current_url: '<?php echo trim(str_replace('//','/',"http://".$_SERVER["HTTP_HOST"].$_SERVER["REQUEST_URI"]."/")); ?>'	};		if (form.length>0)	{				form.submit(function(e) { 			form_id = jQuery(this).attr('id');			e.preventDefault();			var email_check = 1;						<?php			do_action('lp_js_hook_submit_form_pre',null);			?>						if (<?php echo get_option( 'main-landing-page-auto-format-forms' , 1); ?>==1)			{									if (jQuery('.lp-email-value input').val().indexOf('@') == -1)				{										email_check = 0;				}			}						if (email_check)			{								jQuery.post('<?php echo LANDINGPAGES_URLPATH . 'modules/module.ajax-handler.php'; ?>', conversion_data, function(response) {										if (response!=false)					{											<?php						do_action('lp_js_hook_submit_form_success',null);						?>									setTimeout(function(){ 							form.unbind('submit');							jQuery('#'+form_id).submit();						  }, 300 ); 					}									});			}			else			{				form.unbind('submit');				jQuery('#'+form_id).submit();			}		});			}	/***look for link conversion elements and record action***/	lp_stop=0;		var link = jQuery('.lp-track-link');				if (link.length>0)	{		link.live('click',function(){							<?php			do_action('lp_js_hook_click_link_pre',null);			?>						if (lp_stop == 0)			{				jQuery.post('<?php echo LANDINGPAGES_URLPATH . 'modules/module.ajax-handler.php'; ?>', conversion_data, function(response) {										<?php					do_action('lp_js_hook_click_link_success',null);					?>										lp_stop=1;						link.click();					return false;				});									}			else			{				return true;			}						});	}		});</script>